import axios from "axios";
import React, { useEffect, useState } from "react";
import DataTable from "react-data-table-component";
import ArrowDownward from '@material-ui/icons/ArrowDownward';
import Paper from "@mui/material/Paper";

const CriticalUpdate =() => {
  const sortIcon = <ArrowDownward />;
    const [search , setSearch] =useState("");
    const [data , setData] =useState([]);
    const [filterdData , setFilterdData] =useState([]);

const getData =async () => {

    try{
        const response = await axios.get("http://localhost:8082/CriticalUpdate");
        setData(response.data);
        setFilterdData(response.data);
    } catch(err){
        console.log(err);
    }
};

const columns = [
    {
        name:"Knowledge Id",
        selector:(row) => row.knowledge_id,
        sortable:true,

    },

    {
        name:"Product Version Id",
        selector:(row) => row.product_version_id,
        sortable:true
    },

    {
        name:"Knowledge Number",
        selector:(row) => row.knowledge_number,
        sortable:true
    },

    {
      name:"Build Number",
      selector:(row) => row.build_number,
      sortable:true
  },

  {
    name:"File Version",
    selector:(row) => row.file_version,
    sortable:true
},

{
  name:"Release Date",
  selector:(row) => row.release_date,
  sortable:true
  
},

{
  name:"Kb Url",
  selector:(row) => row.kb_url,
  sortable:true
},

{
  name:"Kb Detail",
  selector:(row) => row.kb_detail,
  sortable:true
},


];

useEffect(()=>{
getData();

} ,[]);

useEffect(()=>{
    const result = data.filter(table => {
        return table.kb_detail.toLowerCase().match(search.toLowerCase());
    });
    setFilterdData(result);
}, [search]);

return (
  <div class='Sqlfilter'>
  <Paper>
  <center>
<DataTable 
title= <h3>Critical Update</h3> 
 columns={columns} 

 data={filterdData} 
 pagination
 fixedHeader
 fixedHeaderScrollHeight="500px"
 highlightOnHover
 selectableRowsHighlight
 sortIcon={sortIcon}
 progressComponent
 subHeader
 subHeaderComponent = {
    <input 
    type="text" 
    placeholder="Search Here" 
    className="w-25 form-control"
    subHeaderAlign="center"
    value={search}
    onChange={(e)=>setSearch(e.target.value)}
    />}  
    subHeaderAlign = "centre"
/>
</center>
</Paper>
</div>
);

};

export default CriticalUpdate;
