import React, { useState, useEffect } from 'react';



const KnowledgeBase = () => {
    const [knowledgeBase, setKnowledgeBase] = useState([]);
    const [searchApiData, SetSearchApiData] = useState([]);
    const [filterValue, setFilterValue] = useState('');

        
    useEffect(() => {
        fetch('http://localhost:8082/release2022')
            .then((response) => response.json())
            .then((data) => {
                console.log(data);
                setKnowledgeBase(data);
                SetSearchApiData(data);
            })
            .catch((err) => {
                console.log(err.message);
            });
            
    }, []);


const  handleFilter = (e) => {
    if(e.target.value == ''){
        setKnowledgeBase(searchApiData) 

    } else{

        const filterResult =searchApiData.filter(knowledgeBase => knowledgeBase.knowledge_number.toLowerCase().includes(e.target.value.toLowerCase())|| knowledgeBase.knowledge_id.toLowerCase().includes(e.target.value.toLowerCase()))
if(filterResult.length>0){
     setKnowledgeBase(filterResult)}  
else{
    setKnowledgeBase([{"knowledgeBase_number" : "No Data Found"}])
        setKnowledgeBase(filterResult)
    }

    setFilterValue(e.target.value)
}
   return (
        <div style = {{margin:'20px 20%'}}>
            
            <div className="posts-container">
                <input type= 'search' placeholder = 'Search' value={filterValue} onInput ={(e) =>handleFilter(e)}/>
            </div>
            <table class="tablecls">
                <thead>
                    <tr>
                        <th scope="col">Knowledge Id</th>
                        <th scope="col">product_version_id</th>
                        <th scope="col">Build Number</th>
                        <th scope="col">File Version</th>
                        <th scope="col">Release Date</th>
                        <th scope="col">Kb Url</th>
                        <th scope="col">Kb Detail</th>
                        
                        
                    </tr>
                </thead>
                <tbody>



                   {knowledgeBase.map((knowledgeBase) => {
                        return (



                           <tr>
                                <td>{knowledgeBase.knowledge_id}</td>
                                <td>{knowledgeBase.product_version_id}</td>
                                <td>{knowledgeBase.knowledge_number}</td>
                                <td>{knowledgeBase.build_number}</td>
                                <td>{knowledgeBase.file_version}</td>
                                <td>{knowledgeBase.release_date}</td>
              					<td>{knowledgeBase.kb_url}</td>
                                <td>{knowledgeBase.kb_detail}</td>
                                
                            
                            
                            </tr>
                        );
                    })}
                </tbody>
            </table>
        </div>




    );
}
}

 

export default KnowledgeBase;
